import React, { Component } from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import BooksWindow from './booksWindow';
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';

export default class App extends Component
{
    static displayName = App.name;

    constructor(props) {
        super(props);
        this.state = { activeWindow: "totalBooks", jwt: "", userName: "", loginOpen : false};
    }

    render()
    {
        return (
            <div>
                <Box sx={{ flexGrow: 1 }}>
                    <AppBar position="static">
                        <Toolbar>
                            <Typography variant="h5" component="div" sx={{ flexGrow: 1 }}>
                                Books List
                            </Typography>
                            <Button onClick={this.handleLoginOpen} color="inherit">Login</Button>
                            <Backdrop
                                sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                                open={this.state.loginOpen}
                                onClick={this.handleLoginClose}
                            >
                                <CircularProgress color="inherit" />
                            </Backdrop>
                        </Toolbar>
                    </AppBar>
                </Box>
                <BooksWindow />
            </div>
        );
    }
    async handleLoginOpen() {
        this.setState({ loading: true });
    }
    async handleLoginClose() {
        this.setState({ loading: false })
    }
}
