import React, { Component } from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import BooksWindow from './booksWindow';
import Backdrop from '@mui/material/Backdrop';
import TextField from '@mui/material/TextField';

export default class App extends Component
{
    static displayName = App.name;

    constructor(props) {
        super(props);
        this.state = { activeWindow: "totalBooks", jwt: "", userName : "" , loginOpen: false };//
    }

    render()
    {
        return (
            <div>
                <Box sx={{ flexGrow: 1 }}>
                    <AppBar position="static">
                        <Toolbar>
                            <Typography variant="h5" component="div" sx={{ flexGrow: 1 }}>
                                Books List
                            </Typography>
                            <Button onClick={this.handleLoginOpen.bind(this)} color="inherit">Login</Button>
                            <Backdrop
                                sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                                open={this.state.loginOpen}
                            >
                                <Box
                                    component="form"
                                    sx={{
                                        width: 300,
                                        height: 300,
                                        backgroundColor: 'primary.dark',
                                        '&:hover': {
                                            backgroundColor: 'primary.main',
                                            opacity: [0.9, 0.8, 0.7],
                                        }
                                    }}
                                    noValidate
                                    autoComplete="off"
                                >
                                    <TextField value={this.state.userName} onChange={this.handleLogin.bind(this) }  label="Login" variant="outlined" />
                                    <p>{ this.state.userName }</p>
                                </Box>
                            </Backdrop>
                        </Toolbar>
                    </AppBar>
                </Box>
                <BooksWindow />
            </div>
        );
    }
    handleLoginOpen() {
        this.setState({ loginOpen: true });
    }
    handleLoginClose() {
        this.setState({ loginOpen: false });
    }
    handleLogin(event) {
        this.setState({userName: event.target.value})
    }
}
